Object.assign(state, {
  balls: [],
  animationId: null,
  initializeBalls() {
    const ballCount = 8;
    const balls = [];
    for (let i = 0; i < ballCount; i++) {
      balls.push({
        id: i,
        x: Math.random() * 100,
        y: Math.random() * 100,
        vx: (Math.random() - 0.5) * 0.5,
        vy: (Math.random() - 0.5) * 0.5,
        size: Math.random() * 60 + 20,
        color:
          i % 3 === 0
            ? "rgba(79, 70, 229, 0.1)"
            : i % 3 === 1
            ? "rgba(223, 255, 90, 0.15)"
            : "rgba(228, 232, 239, 0.2)",
      });
    }
    state.balls = balls;
  },
  animateBalls() {
    state.balls = state.balls.map((ball) => {
      let newX = ball.x + ball.vx;
      let newY = ball.y + ball.vy;
      let newVx = ball.vx;
      let newVy = ball.vy;
      if (newX <= 0 || newX >= 100) {
        newVx = -ball.vx;
        newX = Math.max(0, Math.min(100, newX));
      }
      if (newY <= 0 || newY >= 100) {
        newVy = -ball.vy;
        newY = Math.max(0, Math.min(100, newY));
      }
      return {
        ...ball,
        x: newX,
        y: newY,
        vx: newVx,
        vy: newVy,
      };
    });
    state.animationId = requestAnimationFrame(() => state.animateBalls());
  },
  startAnimation() {
    state.initializeBalls();
    state.animateBalls();
  },
  stopAnimation() {
    if (state.animationId) {
      cancelAnimationFrame(state.animationId);
      state.animationId = null;
    }
  },
});

state.startAnimation();
return () => {
  state.stopAnimation();
};
